export class FlvPlugin extends BasePlugin {
    static Flv: typeof Flv;
    /**
     * @param {string | boolean} [mediaType]
     * @param {string} [codec]
     * @returns {boolean}
     * - mediaType: 默认检测 MSE 对 H264 codec是否支持，传入 true 或者配置参数的mediaType的取值检测 WebAssembly是否支持
     * - codec: 暂无使用
     */
    static isSupported(mediaType?: string | boolean, codec?: string): boolean;
    /** @type {Flv} */
    flv: Flv;
    /** @type {PluginExtension} */
    pluginExtension: PluginExtension;
    /** @type {Flv} */
    get core(): Flv;
    /** @type {string} */
    get version(): string;
    /** @type {boolean} */
    get softDecode(): boolean;
    /**
     * @return {import('./flv').Stats | undefined}
     */
    getStats: () => import('./flv').Stats | undefined;
    /**
     *
     * @param {string} url
     * @param {boolean} seamless
     */
    _onSwitchURL: (url: string, seamless: boolean) => void;
    _keepPauseStatus: () => void;
    _onDefinitionChange: ({ to }: {
        to: any;
    }) => void;
    _transError(): void;
    _transCoreEvent(eventName: any): void;
}
import { BasePlugin } from "xgplayer";
import { Flv } from "./flv";
import PluginExtension from "./plugin-extension";
