/**
 * @typedef {Object} VideoFlag
 * @property {number} [isLeading=0]
 * @property {number} [dependsOn=1]
 * @property {number} [isDependedOn=0]
 * @property {number} [hasRedundancy=0]
 * @property {number} [paddingValue=0]
 * @property {number} [degradationPriority=0]
 * @property {number} [isNonSyncSample=1]
 */
export class VideoSample {
    /**
     * @param {number} pts
     * @param {number} dts
     * @param {Uint8Array[]} [units]
     */
    constructor(pts: number, dts: number, units?: Uint8Array[]);
    /** @type {VideoFlag} */
    flag: VideoFlag;
    keyframe: boolean;
    gopId: number;
    duration: number;
    size: number;
    units: any[];
    chromaFormat: number;
    originPts: number;
    pts: number;
    originDts: number;
    dts: number;
    /**
     * @returns {number}
     */
    get cts(): number;
    setToKeyframe(): void;
}
export type VideoFlag = {
    isLeading?: number;
    dependsOn?: number;
    isDependedOn?: number;
    hasRedundancy?: number;
    paddingValue?: number;
    degradationPriority?: number;
    isNonSyncSample?: number;
};
