export class MP4Remuxer {
    /**
     * @param {import('../model').VideoTrack} videoTrack
     * @param {import('../model').AudioTrack} audioTrack
     */
    constructor(videoTrack: import('../model').VideoTrack, audioTrack: import('../model').AudioTrack);
    videoTrack: import("../model").VideoTrack;
    audioTrack: import("../model").AudioTrack;
    /**
     * @returns {import('./fmp4-remuxer').RemuxResult}
     */
    remux(videoTrack: any, audioTrack: any): import('./fmp4-remuxer').RemuxResult;
    _remuxMix(videoTrack: any, audioTrack: any): Uint8Array;
    _remuxTrack(track: any): Uint8Array;
    _remuxData(track: any, chunkOffset: any): {
        mdatData: Uint8Array;
        chunkOffset: any;
    };
    _fillSttsSamples(sttsSamples: any, cur: any, next: any): void;
    _fillCttsSamples(cttsSamples: any, cts: any): void;
    _fillStcoSamples(stcoSamples: any, index: any, samplePerChunk: any, chunkOffset: any): void;
    _fillStscSamples(samples: any, sampleCount: any, samplesPerChunk: any): void;
}
